import axios from "axios";
import Canvas from "canvas";

declare module "gif-in-canvas" {
    export function loadGIF(url: string): Promise<axios.AxiosResponse<any, any>>;
    export function getFrames(data: axios.AxiosResponse<any, any>): Promise<[]>;
    export function getImage(context: Canvas.CanvasRenderingContext2D, width: number, height: number): Uint8ClampedArray;
    export class GIF {
        constructor(width: number, height: number);
        create: (repeat: boolean, delay: number) => void;
        addFrame: (image: Uint8ClampedArray) => void;
        finish: () => BufferSource;
    }
}