module.exports = async function (url) {
  try {
    const regex = /^(?:https?:\/\/)?(?:www\.)?(?:facebook|fb)\.com\/(?:.+\/)?(?:videos?|watch|permalink)\?(?:.*&)?v?=?(\d+)(?:.*)?$/i;
    const result = url.match(regex);
    return result ? result[1] : false;
  } catch (error) {
    return false;
  }
}